import React, { useEffect, useState } from 'react'


function Todolist() {
  const [activity,setActivity]=useState("")
  const [listData,setlistData]=useState([])
  function addActivity(){
    // setlistData([...listData,activity])
    // console.log(listData)
    setlistData((listData)=>{
      const updatedList=[...listData,activity]
      console.log(updatedList)
      setActivity('')
      return updatedList
    })
    function removeActivity(i) {
      const updatedListData=listData.filter((ele,id)=>{
        return i!=id
      })
      setlistData(updatedListData);

    }


  }
  return (
    <>
      <div className='container'></div>
      <div className='header'>Todolist</div>
      <input type='text' placeholder='add activity' value={activity} onChange={
        (e)=>setActivity(e.target.value)}/>
        <button onClick={addActivity}>Add</button>
        <p className='List-heading'>Here is your list:{")"}</p>
        {listData!=[] && listData.map((data,i)=>
        {
          return(
            
            <>
              <p key={i}>
              <div className='listData'>{data}</div>
              <div className='btn-position'><button onClick={()=>removeActivity(i)}>remove-</button></div>

              </p>
            </>
          )
        }
        )}
    </>
  )
}

export default Todolist
